mapper.update:
update emp
set
    username = #{username}
    name = #{name}
    gender = #{gender}
    iamge = #{iamge}
    job = #{job}
    entrydate = #{entrydate}
    dept_id = #{dept_id}

mapper.select:
select *
from emp


        name like concat('%',#{name},'%')


        gender = #{gender}


        entrydate between #{begin} and #{end}


order by create_time desc;
